function btb_change_selection(f){var b=jQuery(f).children("option:selected");var d=jQuery(f).data("event-id");if(jQuery(f).val()===""){jQuery("#btb_direct_booking_checkout_"+d).hide();jQuery("#btb_direct_booking_price_value_"+d).html(jQuery("#btb_direct_booking_price_value_"+d).data("default-price"))}else{var a=b.data("slots");var c=b.data("price");if(a>0){jQuery("#btb_direct_booking_free_slots_"+d).html(a+" "+BTBooking.available)}else{jQuery("#btb_direct_booking_free_slots_"+d).html(BTBooking.fully_booked)}if(c){jQuery("#btb_direct_booking_price_value_"+d).html(c)}jQuery("#btb_direct_booking_amount_"+d).attr({max:a});jQuery("#btb_direct_booking_checkout_"+d).show()}}function btb_change_radio(f){var b=jQuery(f);var d=jQuery(f).data("event-id");if(b.val()===""){jQuery("#btb_direct_booking_checkout_"+d).hide();jQuery("#btb_direct_booking_price_value_"+d).html(jQuery("#btb_direct_booking_price_value_"+d).data("default-price"))}else{var a=b.data("slots");var c=b.data("price");if(a>0){jQuery("#btb_direct_booking_free_slots_"+d).html(a+" "+BTBooking.available)}else{jQuery("#btb_direct_booking_free_slots_"+d).html(BTBooking.fully_booked)}if(c){jQuery("#btb_direct_booking_price_value_"+d).html(c)}jQuery("#btb_direct_booking_amount_"+d).attr({max:a});jQuery("#btb_direct_booking_checkout_"+d).show()}}function btb_clear_selection(b){var a=jQuery(b).data("event-id");jQuery("#btb_direct_booking_checkout_"+a).hide();if(BTBooking.radiolist){jQuery("input#time_0").prop({checked:true})}else{jQuery("#btb_direct_booking_selector_"+a).val("")}jQuery("#btb_direct_booking_price_value_"+a).html(jQuery("#btb_direct_booking_price_value_"+a).data("default-price"));jQuery("#btb_direct_booking_amount_"+a).val(1)}function btb_toggle_button(b){var a=jQuery(b).data("event-id");if(jQuery(b).val()>0){jQuery("#btb_direct_submit_button_"+a).prop({disabled:false})}else{jQuery("#btb_direct_submit_button_"+a).prop({disabled:true})}}jQuery(document).ready(function(){if(BTBooking.radiolist){btb_change_radio(jQuery(".btb_direct_booking_selector input:checked"))}else{btb_change_selection(jQuery(".btb_direct_booking_selector"))}});